pragma solidity ^0.4.24;

import "../node_modules/openzeppelin-solidity/contracts/token/ERC20/StandardToken.sol";

contract ATM  {

    struct Token {
        StandardToken token;
        uint256 index;
    }

    mapping(address => Token) private _tokens;
    address[] public _acceptedTokens;

    constructor(address[] _tokenContracts) public {
        for (uint i = 0; i < _tokenContracts.length; i++) {
            _tokens[_tokenContracts[i]].index = _acceptedTokens.push(_tokenContracts[i]) - 1;
            _tokens[_tokenContracts[i]].token = StandardToken(_tokenContracts[i]);
        }
    }

    function accepts() public view returns (address[]) {
        return _acceptedTokens;
    }

    function addToken(address _tokenContract) public returns (address[]) {
        require(_acceptedTokens[_tokens[_tokenContract].index] != _tokenContract);

        _tokens[_tokenContract].index = _acceptedTokens.push(_tokenContract) - 1;

        return _acceptedTokens;
    }

    function checkSupply(address _tokenContract) public view returns (uint256 _supply) {
        require(_acceptedTokens[_tokens[_tokenContract].index] == _tokenContract);

        _supply = _tokens[_tokenContract].token.totalSupply();

        return _supply;
    }
}
